import { Link, useNavigate } from 'react-router-dom';
import { useState, useEffect } from 'react';
import styles from '../css/Header.module.css';

export default function Header({ user, onLogout }) {
    const navigate = useNavigate();
    const [isNavOpen, setIsNavOpen] = useState(false);

    const maskedPhone = user?.phone?.slice(-4);
    const isAdmin = user?.role === 'admin';
    const isManager = user?.role === 'manager';

    // ‚úÖ Î°úÍ∑∏Ïù∏ ÌõÑ 0.5Ï¥à Îí§ nav ÏûêÎèô Ïó¥Î¶º
    useEffect(() => {
        if (user) {
        const timer = setTimeout(() => setIsNavOpen(true), 0);
        return () => clearTimeout(timer);
        } else {
        setIsNavOpen(false);
        }
    }, [user]);

    return (
        <header className={styles.header}>
        {/* üîù ÏÉÅÎã® Ìó§Îçî */}
        <div className={styles.topBar}>
            <button className={styles.home} onClick={() => navigate('/')}><i className={styles.homeIcon}></i></button>
            <h1 className={styles.brand}>Ìôïsee</h1>
            <div className={styles.auth}>
            {!user ? (
                <Link to="/login">Î°úÍ∑∏Ïù∏</Link>
            ) : (
                <button onClick={onLogout}>Î°úÍ∑∏ÏïÑÏõÉ</button>
            )}
            </div>
        </div>

        {/* üìå ÏûêÎèô Ïä¨ÎùºÏù¥Îìú nav */}
        {user && (
            <nav className={`${styles.navBar} ${isNavOpen ? styles.open : ''}`}>
                <div className={styles.navLeft}>
                    <span><i className={styles.user}></i>{maskedPhone} Îãò</span>
                </div>

                <div className={styles.navCenter}>
                    <Link to="/About">ÏÑúÎπÑÏä§ ÏÜåÍ∞ú</Link>
                    <Link to="/Process">ÏßÑÌñâ ÏïàÎÇ¥</Link>
                    <Link to="/">MyPosts</Link>
                </div>

                <div className={styles.navRight}>
                    {(isAdmin || isManager) && (
                    <Link to="/admin">Í¥ÄÎ¶¨Ïûê</Link>
                    )}
                </div>
            </nav>
        )}
        </header>
    );
}
