import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from '../api/axios';
import styles from '../css/Form.module.css';

export default function LoginForm({ onLogin }) {
  const [form, setForm] = useState({ phone: '', password: '' });
  const [error, setError] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  // ‚úÖ Í≥ÑÏ†ï Ï†ïÎ¶¨ Ï°∞Í±¥: Í±∞Îûò ÏôÑÎ£åÎêú Í∏ÄÎßå ÎÇ®ÏïÑÏûàÏùÑ Îïå
  const checkUserPosts = async (token) => {
    try {
      const res = await axios.get('/posts/my', {
        headers: { Authorization: `Bearer ${token}` }
      });

      const posts = res.data;

      const hasActivePost = posts.some(post => {
        const matched = post.matcher !== null;
        const buyerDone = post.buyerStatus === 'ÏôÑÎ£å';
        const sellerDone = post.sellerStatus === 'ÏôÑÎ£å';
        return matched && (!buyerDone || !sellerDone);
      });

      if (!hasActivePost) {
        await axios.patch('/users/mark-inactive', {}, {
          headers: { Authorization: `Bearer ${token}` }
        });
        console.log('üïí Í≥ÑÏ†ï Ï†ïÎ¶¨ ÏòàÏïΩÎê® (Í±∞Îûò ÏôÑÎ£åÎêú Í∏ÄÎßå Ï°¥Ïû¨)');
      }
    } catch (err) {
      console.warn('‚ö†Ô∏è Í≥ÑÏ†ï Ï†ïÎ¶¨ ÌôïÏù∏ Ïã§Ìå®:', err.message);
    }
  };

  const handleChange = e => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
    setError('');
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await axios.post('/auth/login', form);
      const { token, user } = res.data;

      localStorage.setItem('token', token);
      onLogin(user);
      navigate('/');

      await checkUserPosts(token);
    } catch (err) {
      const msg = err.response?.data?.message;
      if (msg === 'Ï°¥Ïû¨ÌïòÏßÄ ÏïäÎäî ÏÇ¨Ïö©ÏûêÏûÖÎãàÎã§.') {
        setError(msg); // Í∑∏ÎåÄÎ°ú Ï∂úÎ†•
      } else if (msg === 'ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÌãÄÎ†∏ÏäµÎãàÎã§.') {
        setError(msg);
      } else {
        setError(msg || 'Î°úÍ∑∏Ïù∏ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      }
    }
  };

  return (
    <form onSubmit={handleSubmit} className={styles.form}>
      <section>
        <h2>Î°úÍ∑∏Ïù∏</h2>

        <div className={styles.regFormbox}>
          <input
            type="text"
            name="phone"
            placeholder="Ï†ÑÌôîÎ≤àÌò∏"
            value={form.phone}
            onChange={handleChange}
            required
            className={styles.input}
          />

          <div className={styles.passwordWrapper}>
            <input
              type={showPassword ? 'text' : 'password'}
              name="password"
              placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
              value={form.password}
              onChange={handleChange}
              required
              className={styles.input}
            />
            <button
              type="button"
              onClick={() => setShowPassword(prev => !prev)}
              className={styles.toggle}
            >
              {showPassword ? 'üôà Ïà®Í∏∞Í∏∞' : 'üëÅÔ∏è ÏïîÌò∏Î≥¥Í∏∞'}
            </button>
          </div>

          {error && <div className={styles.error}>{error}</div>}

          <button type="submit">Î°úÍ∑∏Ïù∏</button>
        </div>
      </section>
    </form>
  );
}
