import { useState } from 'react';
import styles from '../css/Posts.module.css';
import ShippingForm from './ShippingForm';
import TrackingModal from './TrackingModal';

export default function SellerDispatchForm({ post, updatePost }) {
  const {
    sellerStatus,
    inspectionResult,
    shipping,
    settlementAccount,
    paymentMethod,
  } = post;

  const [courier, setCourier] = useState(shipping?.courier || '');
  const [tracking, setTracking] = useState(shipping?.tracking || '');
  const [isEditing, setIsEditing] = useState(false);
  const [showTrackingModal, setShowTrackingModal] = useState(false);

  const [bank, setBank] = useState(settlementAccount?.bank || '');
  const [account, setAccount] = useState(settlementAccount?.account || '');
  const isAccountSubmitted = !!settlementAccount?.bank;

  const canInputShipping = sellerStatus === 'ÏßÑÌñâ Ï†Ñ';
  const canModifyShipping = sellerStatus === 'Î∞úÏÜ°';
  const isWaitingForInspection =
    ['ÏûÖÍ≥†', 'Î¨ºÌíàÌôïÏù∏'].includes(sellerStatus) && inspectionResult === null;
  const canSubmitAccount =
    sellerStatus === 'Î¨ºÌíàÌôïÏù∏' && inspectionResult === 'Ï†ïÏÉÅ' && !isAccountSubmitted;
  const canSubmitErr =
    sellerStatus === 'Î¨ºÌíàÌôïÏù∏' && inspectionResult === 'Ïù¥ÏÉÅ' && !isAccountSubmitted;
  const canFinalize = sellerStatus === 'Ï†ïÏÇ∞';

  const handleDispatch = async () => {
    if (!courier || !tracking) {
      alert('ÌÉùÎ∞∞ÏÇ¨ÏôÄ Ïö¥ÏÜ°Ïû• Î≤àÌò∏Î•º Î™®Îëê ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    try {
      await updatePost({
        sellerStatus: 'Î∞úÏÜ°',
        shipping: { courier, tracking, dispatchedAt: new Date() },
      });
      alert('üì¶ Î∞úÏÜ° Ï≤òÎ¶¨ ÏôÑÎ£å');
    } catch (err) {
      alert('ÏÜ°Ïû• Îì±Î°ù Ïã§Ìå®: ' + (err.response?.data?.message || 'ÏÑúÎ≤Ñ Ïò§Î•ò'));
    }
  };

  const handleTrackingUpdate = async () => {
    if (!courier || !tracking) {
      alert('ÏÜ°Ïû• ÏàòÏ†ï Ïãú ÌÉùÎ∞∞ÏÇ¨ÏôÄ Ïö¥ÏÜ°Ïû• Î≤àÌò∏Í∞Ä ÌïÑÏöîÌï©ÎãàÎã§.');
      return;
    }

    try {
      await updatePost({ shipping: { courier, tracking } });
      alert('‚úèÔ∏è ÏÜ°Ïû• Ï†ïÎ≥¥Í∞Ä ÏàòÏ†ïÎêòÏóàÏäµÎãàÎã§.');
      setIsEditing(false);
    } catch (err) {
      alert('ÏÜ°Ïû• ÏàòÏ†ï Ïã§Ìå®: ' + (err.response?.data?.message || 'ÏÑúÎ≤Ñ Ïò§Î•ò'));
    }
  };

  const handleAccountSubmit = async () => {
    if (!bank || !account) {
      alert('ÏùÄÌñâÎ™ÖÍ≥º Í≥ÑÏ¢åÎ≤àÌò∏Î•º Î™®Îëê ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    try {
      await updatePost({
        settlementAccount: { bank, account },
        sellerStatus: 'Ï†ïÏÇ∞',
      });
      alert('üí∞ Ï†ïÏÇ∞ Í≥ÑÏ¢åÍ∞Ä Îì±Î°ùÎêòÏóàÏäµÎãàÎã§.');
    } catch (err) {
      alert('Í≥ÑÏ¢å Îì±Î°ù Ïã§Ìå®: ' + (err.response?.data?.message || 'ÏÑúÎ≤Ñ Ïò§Î•ò'));
    }
  };

  return (
    <div className={styles.dispatchBox}>
      <h4>üì¶ ÌåêÎß§Ïûê</h4>

      {/* ÏÜ°Ïû• ÏûÖÎ†• */}
      {canInputShipping && (
        <>
          <ShippingForm
            courier={courier}
            tracking={tracking}
            setCourier={setCourier}
            setTracking={setTracking}
          />
          <button onClick={handleDispatch} className={styles.confirmBtn}>
            üì¶ Î∞úÏÜ° Ï≤òÎ¶¨
          </button>
        </>
      )}

      {/* ÏÜ°Ïû• ÏàòÏ†ï & Î∞∞ÏÜ° Ï∂îÏ†Å */}
      {canModifyShipping && (
        <>
          {!isEditing ? (
            <div className={styles.buttonGroup}>
              <button onClick={() => setIsEditing(true)}>‚úèÔ∏è ÏÜ°Ïû• ÏàòÏ†ï</button>
              <button onClick={() => setShowTrackingModal(true)}>üîç Î∞∞ÏÜ° Ï∂îÏ†Å</button>
            </div>
          ) : (
            <>
              <ShippingForm
                courier={courier}
                tracking={tracking}
                setCourier={setCourier}
                setTracking={setTracking}
              />
              <div className={styles.buttonGroup}>
                <button onClick={handleTrackingUpdate}>‚úÖ ÏàòÏ†ï ÏôÑÎ£å</button>
                <button onClick={() => setIsEditing(false)}>‚ùå Ï∑®ÏÜå</button>
              </div>
            </>
          )}
        </>
      )}

      {/* Î∞∞ÏÜ° Ï∂îÏ†Å Î™®Îã¨ */}
      {showTrackingModal && (
        <TrackingModal
          courier={courier}
          tracking={tracking}
          onClose={() => setShowTrackingModal(false)}
        />
      )}

      {/* Í≤ÄÏÇ¨ Í≤∞Í≥º ÎåÄÍ∏∞ */}
      {isWaitingForInspection && (
        <div className={styles.notice}>‚è≥ Í¥ÄÎ¶¨ÏûêÍ∞Ä Î¨ºÌíàÏùÑ ÌôïÏù∏ Ï§ëÏûÖÎãàÎã§.</div>
      )}
      {canSubmitErr && (
        <div className={styles.small_noti}>
          ‚ñ† Î¨ºÌíàÏóê Ïù¥ÏÉÅÏù¥ Î∞úÍ≤¨ÎêòÏóàÏäµÎãàÎã§. Íµ¨Îß§ÏûêÏôÄ Ïó∞ÎùΩÏùÑ Í∂åÌï©ÎãàÎã§.
        </div>
      )}

      {/* Ï†ïÏÇ∞ Í≥ÑÏ¢å ÏûÖÎ†• */}
      {canSubmitAccount && (
        <div className={styles.notice}>
          <h5>üí∞ ÌåêÎß§ ÎåÄÍ∏àÏùÑ Î∞õÏúºÏã§ Í≥ÑÏ¢åÎ•º ÏûÖÎ†•Ìï¥ Ï£ºÏÑ∏Ïöî</h5>
          <input
            type="text"
            placeholder="ÏùÄÌñâÎ™Ö"
            value={bank}
            onChange={(e) => setBank(e.target.value)}
            className={styles.input}
          />
          <input
            type="text"
            placeholder="Í≥ÑÏ¢åÎ≤àÌò∏"
            value={account}
            onChange={(e) => setAccount(e.target.value)}
            className={styles.input}
          />
          <button onClick={handleAccountSubmit} className={styles.confirmBtn}>
            ‚úÖ Í≥ÑÏ¢å ÏûÖÎ†•
          </button>
        </div>
      )}

      {/* Ï†ïÏÇ∞ Ï†ïÎ≥¥ ÌëúÏãú */}
      {sellerStatus === 'Ï†ïÏÇ∞' && (
        <div className={styles.notice}>
          <p>üí≥ Í≤∞Ï†ú Î∞©Ïãù: {paymentMethod === 'card' ? 'Ïπ¥Îìú Í≤∞Ï†ú' : 'Î¨¥ÌÜµÏû•ÏûÖÍ∏à'}</p>
          {settlementAccount?.bank && (
            <p>üí∞ Ï†ïÏÇ∞ Í≥ÑÏ¢å: {settlementAccount.bank} / {settlementAccount.account}</p>
          )}
        </div>
      )}

      {/* Ï†ïÏÇ∞ ÌôïÏù∏ */}
      {canFinalize && (
        <button
          onClick={() => updatePost({ sellerStatus: 'ÏôÑÎ£å' })}
          className={styles.confirmBtn}
        >
          üéâ Ï†ïÏÇ∞ ÌôïÏù∏
        </button>
      )}
    </div>
  );
}
