import { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import PostRegister from '../components/PostRegister';
import PostList from '../components/PostList';
import axios from '../api/axios';
import styles from '../css/Posts.module.css';

export default function MyPosts({ user }) {
  const [posts, setPosts] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [matchCodeInput, setMatchCodeInput] = useState('');
  const [availablePosts, setAvailablePosts] = useState([]);
  const token = localStorage.getItem('token');
  const navigate = useNavigate();

  // ‚úÖ Í∏Ä Ï°∞Ìöå
  const fetchPosts = useCallback(async () => {
    try {
      const res = await axios.get('/posts', {
        headers: { Authorization: `Bearer ${token}` }
      });

      const allPosts = res.data;

      const myPosts = allPosts.filter(post =>
        post.author === user._id ||
        post.matcher === user._id ||
        user.role === 'admin'
      );

      const matchablePosts = allPosts.filter(post =>
        post.author !== user._id && !post.matcher
      );

      setPosts(myPosts);
      setAvailablePosts(matchablePosts);
    } catch (err) {
      console.error('Í∏Ä Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:', err.message);
    }
  }, [user._id, user.role, token]);

  useEffect(() => {
    fetchPosts();
  }, [fetchPosts]);

  // ‚úÖ Í∏Ä Îì±Î°ù
  const handlePostCreated = newPost => {
    setPosts(prev => [newPost, ...prev]);
    setShowForm(false);
  };

  // ‚úÖ Í∏Ä ÏàòÏ†ï
  const handleUpdate = updatedPost => {
    setPosts(prev =>
      prev.map(post => post._id === updatedPost._id ? updatedPost : post)
    );
  };

  const handleRefresh = () => {
    fetchPosts();
  };

  // ‚úÖ Îß§Ïπ≠ÏΩîÎìú ÏûÖÎ†•
  const handleMatchCode = async (code) => {
    if (!code || code.length < 4) {
      alert('Îß§Ïπ≠ÏΩîÎìúÎ•º Ï†ïÌôïÌûà ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    try {
      const res = await axios.post('/posts/match', { matchCode: code }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Îß§Ïπ≠ ÏÑ±Í≥µ!');
      navigate(`/posts/${res.data.post._id}`);
    } catch (err) {
      const msg = err.response?.data?.message || 'ÏÑúÎ≤Ñ Ïò§Î•ò';
      alert(`Îß§Ïπ≠ Ïã§Ìå®: ${msg}`);
    }
  };

  return (
    <section>
      <div className={styles.container}>
        <h2 className={styles.tit}>My Posts</h2>

        {/* Îì±Î°ù Î≤ÑÌäº */}
        <button
          onClick={() => setShowForm(prev => !prev)}
          className={styles.registerBtn}
        >
          {showForm ? 'Îì±Î°ùÏ∑®ÏÜå' : 'Î¨ºÌíàÎì±Î°ù'}
        </button>

        {/* Í∏Ä Îì±Î°ù Ìèº */}
        {showForm && (
          <PostRegister
            onPostCreated={handlePostCreated}
            onCancel={() => setShowForm(false)}
          />
        )}

        {/* ÎÇ¥ Í∏Ä Î™©Î°ù */}
        {posts.length > 0 && (
          <PostList
            posts={posts}
            currentUserId={user._id}
            userRole={user.role}
            onRefresh={handleRefresh}
            onUpdate={handleUpdate}
          />
        )}

        {/* Îß§Ïπ≠ÏΩîÎìú ÏûÖÎ†• */}
        <div className={styles.matchBox}>
          <h3><i className={styles.matchButton} style={{ color: '#000', marginTop: '12px' }}></i>Îß§Ïπ≠ÏΩîÎìúÎ°ú Ïó∞Í≤∞ÌïòÍ∏∞</h3>
          <input
            type="text"
            value={matchCodeInput}
            onChange={e => setMatchCodeInput(e.target.value.toUpperCase())}
            placeholder="Îß§Ïπ≠ÏΩîÎìúÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            className={styles.input}
          />
          <button onClick={() => handleMatchCode(matchCodeInput)}>
            <i className={styles.matchButton}></i>Îß§Ïπ≠ÌïòÍ∏∞
          </button>
        </div>

        {/* Í¥ÄÎ¶¨Ïûê ÌÖåÏä§Ìä∏Ïö© Îß§Ïπ≠Í∏Ä */}
        {user.role === 'admin' && (
          <>
            <h3>üß™ Îß§Ïπ≠ Í∞ÄÎä•Ìïú Í∏Ä (ÌÖåÏä§Ìä∏Ïö©)</h3>
            {availablePosts.length === 0 ? (
              <p>ÌòÑÏû¨ Îß§Ïπ≠ Í∞ÄÎä•Ìïú Í∏ÄÏù¥ ÏóÜÏäµÎãàÎã§.</p>
            ) : (
              <div className={styles.matchList}>
                {availablePosts.map(post => (
                  <div key={post._id} className={styles.card}>
                    <h4>{post.title}</h4>
                    <p>Í∏àÏï°: {post.price.toLocaleString()}Ïõê</p>
                    <p>Îß§Ïπ≠ÏΩîÎìú: <strong>{post.matchCode}</strong></p>
                    <button onClick={() => handleMatchCode(post.matchCode)}>
                      ü§ù Îß§Ïπ≠ÌïòÍ∏∞
                    </button>
                  </div>
                ))}
              </div>
            )}
          </>
        )}
      </div>
    </section>
  );
}
