import { useState } from 'react';
import axios from '../api/axios';
import { getFeeSummary } from '../utils/feeSummary';
import TransactionNotice from './TransactionNotice';
import styles from '../css/Posts.module.css';

export default function PostRegister({ onPostCreated, onCancel }) {
  const [type, setType] = useState('seller');
  const [title, setTitle] = useState('');
  const [price, setPrice] = useState('');
  const [link, setLink] = useState('');
  const [feeResponsibility, setFeeResponsibility] = useState('buyer');
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [error, setError] = useState('');
  const token = localStorage.getItem('token');

  const generateMatchCode = () =>
    Math.random().toString(36).substring(2, 8).toUpperCase();

  const normalizeLink = url => {
    if (!/^https?:\/\//i.test(url)) {
      return 'http://' + url;
    }
    return url;
  };

  const handleSubmit = async () => {
    if (!title || !price || !link) {
      setError('Î™®Îì† Ìï≠Î™©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    const normalizedLink = normalizeLink(link);

    try {
      const matchCode = generateMatchCode();
      const summary = getFeeSummary({ price, feeResponsibility, paymentMethod });

      const res = await axios.post('/posts', {
        type,
        title,
        price: parseInt(price, 10),
        link: normalizedLink, // ‚úÖ Î≥¥Ï†ïÎêú ÎßÅÌÅ¨ ÏÇ¨Ïö©
        matchCode,
        feeResponsibility,
        paymentMethod,
        buyerAmount: summary.buyerAmount,
        sellerAmount: summary.sellerAmount,
        totalAmount: summary.totalAmount,
        feeAmount: summary.feeAmount
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      onPostCreated(res.data);
      setTitle('');
      setPrice('');
      setLink('');
      setFeeResponsibility('buyer');
      setPaymentMethod('cash');
      setError('');
    } catch (err) {
      setError('Îì±Î°ù Ïã§Ìå®. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
    }
  };

  const handleCancel = () => {
    setTitle('');
    setPrice('');
    setLink('');
    setFeeResponsibility('buyer');
    setPaymentMethod('cash');
    setError('');
    if (onCancel) onCancel();
  };

  const summary = getFeeSummary({ price, feeResponsibility, paymentMethod });

  return (
    <section>
    <div className={styles.card}>
      <h3>üìù Î¨ºÌíà Îì±Î°ù</h3>

      <p className={styles.small_noti}>‚ñ† ÏûÖÍ∏à(Íµ¨Îß§Ïûê)Í≥º Î∞úÏÜ°(ÌåêÎß§Ïûê) Ïù¥ÌõÑÎ∂ÄÌÑ∞ ÏàòÏàòÎ£åÍ∞Ä Î∞úÏÉùÌï©ÎãàÎã§.</p>
      <p className={styles.small_sub}>‚ñ† Îß§Ïπ≠ÎêòÏßÄ ÏïäÎäî Í∏ÄÏùÄ 7Ïùº ÌõÑ ÏÇ≠Ï†úÎê©ÎãàÎã§.</p>

      {/* Ïó≠Ìï† ÏÑ†ÌÉù */}
      <div className={styles.radioGroup}>
        <label><input type="radio" value="seller" checked={type === 'seller'} onChange={() => setType('seller')} /> ÌåêÎß§Ïûê</label>
        <label><input type="radio" value="buyer" checked={type === 'buyer'} onChange={() => setType('buyer')} /> Íµ¨Îß§Ïûê</label>
      </div>

      {/* Ï†úÌíà Ï†ïÎ≥¥ ÏûÖÎ†• */}
      <input type="text" placeholder="Ï†úÌíàÎ™Ö" value={title} onChange={e => setTitle(e.target.value)} className={styles.input} />
      <input type="number" min={50000} step={1000} placeholder="ÏµúÌïò Í±∞ÎûòÍ∞ÄÎäî 50,000ÏõêÏù¥Î©∞ 1,000Îã®ÏúÑ Ï°∞Ï†ïÎê©ÎãàÎã§." value={price} onChange={e => setPrice(e.target.value)} className={styles.input} />
      <input type="text" placeholder="Î¨ºÌíà ÎßÅÌÅ¨Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî (Ïòà: naver.com/item/123)" value={link} onChange={e => setLink(e.target.value)} className={styles.input} />

      {/* ÏàòÏàòÎ£å Î∂ÄÎã¥ ÏÑ†ÌÉù */}
      <div className={styles.radioGroup}>
        <label><input type="radio" value="buyer" checked={feeResponsibility === 'buyer'} onChange={() => setFeeResponsibility('buyer')} /> Íµ¨Îß§Ïûê Î∂ÄÎã¥</label>
        <label><input type="radio" value="seller" checked={feeResponsibility === 'seller'} onChange={() => setFeeResponsibility('seller')} /> ÌåêÎß§Ïûê Î∂ÄÎã¥</label>
        <label><input type="radio" value="split" checked={feeResponsibility === 'split'} onChange={() => setFeeResponsibility('split')} /> Î∞òÎ∞ò Î∂ÄÎã¥</label>
      </div>

      {/* Í≤∞Ï†ú Î∞©Ïãù ÏÑ†ÌÉù */}
      <div className={styles.radioGroup}>
        <label><input type="radio" value="cash" checked={paymentMethod === 'cash'} onChange={() => setPaymentMethod('cash')} /> ÌòÑÍ∏à ÏûÖÍ∏à</label>
        <label><input type="radio" value="pay" checked={paymentMethod === 'pay'} onChange={() => setPaymentMethod('pay')} /> Ï†ÑÏûê Í≤∞Ï†ú</label>
      </div>

      {/* ÏàòÏàòÎ£å ÏïàÎÇ¥ */}
      {summary && (
        <pre className={styles.feeNotice}>
          <p>ÏàòÏàòÎ£å: {summary.feeAmount.toLocaleString()}Ïõê</p>
          <p>Íµ¨Îß§Ïûê Í≤∞Ï†ú Í∏àÏï°: {summary.buyerAmount.toLocaleString()}Ïõê</p>
          <p>ÌåêÎß§Ïûê Ï†ïÏÇ∞ Í∏àÏï°: {summary.sellerAmount.toLocaleString()}Ïõê</p>
          {/* <p>Ï¥ù Í±∞Îûò Í∏àÏï°: {summary.totalAmount.toLocaleString()}Ïõê</p> */}
        </pre>
      )}

      {/* Î≤ïÏ†Å Î¨∏Ï†úÏÜåÏßÄ ÏïàÎÇ¥ */}
      <TransactionNotice />

      <div className={styles.buttonGroup}>
        <button onClick={handleSubmit} className={styles.registerBtn}>Îì±Î°ùÌïòÍ∏∞</button>
        <button onClick={handleCancel} className={styles.cancelBtn}>Îì±Î°ùÏ∑®ÏÜå</button>
      </div>

      {error && <p className={styles.error}>{error}</p>}
    </div>
    </section>
  );
}
