import { useEffect, useState } from 'react';
import styles from '../css/Form.module.css';
import axios from '../api/axios'; // ÎòêÎäî fetch ÏÇ¨Ïö© Í∞ÄÎä•

export default function TrackingModal({ courier, tracking, onClose }) {
    const [status, setStatus] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchStatus = async () => {
        try {
            const res = await axios.get(
            `https://api.sweettracker.co.kr/api/v1/trackingInfo?t_code=${courier}&t_invoice=${tracking}&apiKey=YOUR_API_KEY`
            );
            setStatus(res.data);
        } catch (err) {
            setError('Î∞∞ÏÜ° Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.');
        }
        };

        fetchStatus();
    }, [courier, tracking]);

    return (
        <div className={styles.overlay}>
        <div className={styles.modal}>
            <button onClick={onClose} className={styles.closeBtn}>‚úñ</button>
            <h4>üì¶ Î∞∞ÏÜ° Ï∂îÏ†Å</h4>
            {error && <p className={styles.error}>{error}</p>}
            {status ? (
            <div className={styles.statusBox}>
                <p>üöö ÌÉùÎ∞∞ÏÇ¨: {status.courierName}</p>
                <p>üìÑ ÏÜ°Ïû•Î≤àÌò∏: {status.invoiceNo}</p>
                <p>üìç ÌòÑÏû¨ ÏÉÅÌÉú: <strong>{status.status}</strong></p>
                <p>üïí ÏµúÍ∑º ÏúÑÏπò: {status.lastDetail?.location} ({status.lastDetail?.time})</p>
            </div>
            ) : (
            !error && <p>‚è≥ Î∞∞ÏÜ° Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ëÏûÖÎãàÎã§...</p>
            )}
        </div>
        </div>
    );
}
