import { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from '../api/axios';
import styles from '../css/Posts.module.css';

import BuyerActions from './BuyerActions';
import SellerDispatchForm from './SellerDispatchForm';
import AdminActions from './AdminActions';
import MatchButton from './MatchButton';
import TransactionNotice from './TransactionNotice';

export default function PostViewer({ userRole, currentUserId }) {
  const { id } = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState(null);
  const token = localStorage.getItem('token');

  // ‚úÖ Í∏Ä Ï°∞Ìöå
  useEffect(() => {
    if (!id || id === 'admin') {
      navigate('/admin/posts'); // ÏûòÎ™ªÎêú Ï†ëÍ∑º Î∞©Ïñ¥
      return;
    }

    axios.get(`/posts/${id}`)
      .then(res => setPost(res.data))
      .catch(() => navigate('/posts'));
  }, [id, navigate]);

  // ‚úÖ Í∏Ä ÏóÖÎç∞Ïù¥Ìä∏
  const updatePost = async (patchData) => {
    try {
      const res = await axios.patch(`/posts/${id}`, patchData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPost(res.data);
    } catch (err) {
      alert('ÏóÖÎç∞Ïù¥Ìä∏ Ïã§Ìå®');
    }
  };

  // ‚úÖ Îß§Ïπ≠ Ï≤òÎ¶¨
  const handleMatchCode = async () => {
    try {
      const res = await axios.post('/posts/match', { matchCode: post.matchCode }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPost(res.data.post);
      alert('Îß§Ïπ≠Ïù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§!');
    } catch (err) {
      alert('Îß§Ïπ≠ Ïã§Ìå®: ' + (err.response?.data?.message || 'ÏÑúÎ≤Ñ Ïò§Î•ò'));
    }
  };

  if (!post) return null;

  // ‚úÖ Í∂åÌïú Î∞è Ïó≠Ìï† Î∂ÑÍ∏∞
  const isAuthor = post.author?.toString() === currentUserId;
  const isMatcher = post.matcher?.toString() === currentUserId;
  const isAdmin = userRole === 'admin';
  const canView = isAuthor || isMatcher || isAdmin;
  const canMatch = !post.matcher && !isAuthor && userRole === 'user';

  const isBuyer = (isAuthor && post.type === 'buyer') || (isMatcher && post.matcherRole === 'buyer');
  const isSeller = (isAuthor && post.type === 'seller') || (isMatcher && post.matcherRole === 'seller');
  const canCopyMatchCode = post.status === 'Îì±Î°ù' && post.matchCode;

  // ‚úÖ Í±∞Îûò ÏôÑÎ£å Ï°∞Í±¥
  const isFullyCompleted =
    post.buyerStatus === 'ÏôÑÎ£å' &&
    post.sellerStatus === 'ÏôÑÎ£å' &&
    !!post.settlementAccount?.bank;

  // ‚úÖ Î∞∞ÏÜ° Ï†ïÎ≥¥ ÎÖ∏Ï∂ú Ï°∞Í±¥
  const shouldShowShippingInfo =
    isBuyer &&
    ['Ï∂úÍ≥†', 'ÏôÑÎ£å'].includes(post.buyerStatus) &&
    post.shipping?.courier &&
    post.shipping?.tracking &&
    post.sellerStatus === 'Î∞úÏÜ°';

  if (!canView) {
    return <div className={styles.main}>üö´ Ïù¥ Í∏ÄÏóê Ï†ëÍ∑ºÌï† Ïàò ÏóÜÏäµÎãàÎã§.</div>;
  }

  // ‚úÖ ÏÉÅÌÉú ÌÖçÏä§Ìä∏ Î∞è ÏÉâÏÉÅ Í≤∞Ï†ï
  let statusText = 'Îì±Î°ù Ï§ë';
  let statusClass = styles.waiting;

  if (post.status === 'Îß§Ïπ≠') {
    statusText = 'Í±∞Îûò ÏßÑÌñâ Ï§ë >>';
    statusClass = styles.active;
  } else if (post.status === 'ÏôÑÎ£å') {
    statusText = 'Í±∞Îûò ÏôÑÎ£å';
    statusClass = styles.done;
  }

  return (
    <section>
      <div className={styles.card}>
        {/* üìÑ Í∏Ä Ï†ïÎ≥¥ */}
        <h3>{post.title}</h3>
        <p>‚ñ∂ Í∏àÏï°: {post.price.toLocaleString()}Ïõê</p>
        <a href={post.link} target="_blank" rel="noopener noreferrer" className={styles.link}>
          üîó Î¨ºÌíà Ìè¨Ïä§ÌåÖ Î≥¥Í∏∞
        </a>
        <p className={`${styles.status} ${statusClass}`}>{statusText}</p>

        {/* üîó Îß§Ïπ≠ ÏΩîÎìú */}
        <div className={styles.matBox}>          
          {post.matchCode && (
            <p className={styles.codeBox}>
              ‚ñ∂ Îß§Ïπ≠ ÏΩîÎìú: <strong>{post.matchCode}</strong>
            </p>          
          )}

          {canCopyMatchCode && (
            <button className={styles.copyBtn} onClick={() => {
                if (navigator?.clipboard?.writeText) {
                  navigator.clipboard.writeText(post.matchCode)
                  .then(() => alert('üìã Îß§Ïπ≠ÏΩîÎìúÍ∞Ä Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§'))
                  .catch(() => alert('‚ùå Î≥µÏÇ¨Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§'));
                } else {
                  alert('‚ùå Ïù¥ Î∏åÎùºÏö∞Ï†ÄÏóêÏÑúÎäî Î≥µÏÇ¨ Í∏∞Îä•ÏùÑ ÏßÄÏõêÌïòÏßÄ ÏïäÏäµÎãàÎã§');
                }
              }}
            >
              üìã ÏΩîÎìú Î≥µÏÇ¨
            </button>
          )}
        </div>

        {/* üí∞ ÏàòÏàòÎ£å ÏïàÎÇ¥ */}
        <pre className={styles.feeNotice}>
          <p>ÏàòÏàòÎ£å: {post.feeAmount?.toLocaleString()}Ïõê</p>
          <p>Íµ¨Îß§Ïûê Í≤∞Ï†ú Í∏àÏï°: {post.buyerAmount?.toLocaleString()}Ïõê</p>
          <p>ÌåêÎß§Ïûê Ï†ïÏÇ∞ Í∏àÏï°: {post.sellerAmount?.toLocaleString()}Ïõê</p>
          {/* <p>Ï¥ù Í±∞Îûò Í∏àÏï°: {post.totalAmount?.toLocaleString()}Ïõê</p> */}
        </pre>

        <TransactionNotice />

        {/* ‚úÖ Í±∞Îûò Ï¢ÖÎ£å ÏïàÎÇ¥ */}
        {isFullyCompleted && (
          <div className={styles.completeBanner}>
            üéâ Í±∞ÎûòÍ∞Ä ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§.
          </div>
        )}

        {/* üìä ÏÉÅÌÉú ÌëúÏãú */}
        {!isFullyCompleted && (
          <div className={styles.statusBox}>
            <p>üßæ Íµ¨Îß§Ïûê ÏÉÅÌÉú: <strong>{post.buyerStatus || 'ÏßÑÌñâ Ï†Ñ'}</strong></p>
            <p>üì¶ ÌåêÎß§Ïûê ÏÉÅÌÉú: <strong>{post.sellerStatus || 'ÏßÑÌñâ Ï†Ñ'}</strong></p>

            {shouldShowShippingInfo && (
              <div className={styles.notice}>
                <p>üöö Î∞∞ÏÜ° Ï†ïÎ≥¥: {post.shipping.courier} / {post.shipping.tracking}</p>
              </div>
            )}
          </div>
        )}

        {/* üéØ Ïï°ÏÖò ÏòÅÏó≠ */}
        {!isFullyCompleted && (
          <div className={styles.actions}>
            {canMatch && <MatchButton onClick={handleMatchCode} />}

            {post.matcher && (
              <>
                {isAdmin && (
                  <AdminActions post={post} updatePost={updatePost} />
                )}

                {(isBuyer || isAdmin) && (
                  <BuyerActions post={post} updatePost={updatePost} />
                )}

                {(isSeller || isAdmin) && (
                  <SellerDispatchForm post={post} updatePost={updatePost} isAdmin={isAdmin} />
                )}
              </>
            )}
          </div>
        )}

        {/* ‚¨ÖÔ∏è Îí§Î°úÍ∞ÄÍ∏∞ */}
        <div className={styles.buttom_controll}>
          <button onClick={() => navigate(-1)} className={styles.backBtn}>
            <i className={styles.arrowLeft}></i>Îí§Î°úÍ∞ÄÍ∏∞
          </button>
        </div>
      </div>
    </section>
  );
}
