import { useState } from 'react';
import axios from '../api/axios';
import styles from '../css/Posts.module.css';
import ShippingForm from './ShippingForm';
import TrackingStatus from './TrackingStatus';

export default function SellerDispatchForm({ post, updatePost, isAdmin }) {
  const token = localStorage.getItem('token');
  const sellerStatus = post.sellerStatus;
  const inspectionResult = post.inspectionResult;

  const [courier, setCourier] = useState(post.shipping?.courier || '');
  const [tracking, setTracking] = useState(post.shipping?.tracking || '');
  const [trackingInfo, setTrackingInfo] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  const [bank, setBank] = useState(post.settlementAccount?.bank || '');
  const [account, setAccount] = useState(post.settlementAccount?.account || '');
  const isAccountSubmitted = !!post.settlementAccount?.bank;

  // ‚úÖ ÏÉÅÌÉú Î∂ÑÍ∏∞
  const canInputShipping = sellerStatus === 'ÏßÑÌñâ Ï†Ñ';
  const canModifyShipping = sellerStatus === 'Î∞úÏÜ°';
  const isWaitingForInspection = ['ÏûÖÍ≥†', 'Î¨ºÌíàÌôïÏù∏'].includes(sellerStatus) && inspectionResult === null;
  const canSubmitAccount = sellerStatus === 'Î¨ºÌíàÌôïÏù∏' && inspectionResult === 'Ï†ïÏÉÅ' && !isAccountSubmitted;
  const canSubmitErr = sellerStatus === 'Î¨ºÌíàÌôïÏù∏' && inspectionResult === 'Ïù¥ÏÉÅ' && !isAccountSubmitted;
  const canFinalize = sellerStatus === 'Ï†ïÏÇ∞';

  // ‚úÖ Î∞úÏÜ° Ï≤òÎ¶¨
  const handleDispatch = async () => {
    if (!courier || !tracking) {
      alert('ÌÉùÎ∞∞ÏÇ¨ÏôÄ Ïö¥ÏÜ°Ïû• Î≤àÌò∏Î•º Î™®Îëê ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    try {
      await updatePost({
        sellerStatus: 'Î∞úÏÜ°',
        shipping: {
          courier,
          tracking,
          dispatchedAt: new Date()
        }
      });
      alert('üì¶ Î∞úÏÜ° Ï≤òÎ¶¨ ÏôÑÎ£å');
    } catch (err) {
      alert('ÏÜ°Ïû• Îì±Î°ù Ïã§Ìå®: ' + (err.response?.data?.message || 'ÏÑúÎ≤Ñ Ïò§Î•ò'));
    }
  };

  // ‚úÖ ÏÜ°Ïû• ÏàòÏ†ï
  const handleTrackingUpdate = async () => {
    if (!courier || !tracking) {
      alert('ÏÜ°Ïû• ÏàòÏ†ï Ïãú ÌÉùÎ∞∞ÏÇ¨ÏôÄ Ïö¥ÏÜ°Ïû• Î≤àÌò∏Í∞Ä ÌïÑÏöîÌï©ÎãàÎã§.');
      return;
    }

    try {
      await updatePost({ shipping: { courier, tracking } });
      alert('‚úèÔ∏è ÏÜ°Ïû• Ï†ïÎ≥¥Í∞Ä ÏàòÏ†ïÎêòÏóàÏäµÎãàÎã§.');
      setIsEditing(false);
    } catch (err) {
      alert('ÏÜ°Ïû• ÏàòÏ†ï Ïã§Ìå®: ' + (err.response?.data?.message || 'ÏÑúÎ≤Ñ Ïò§Î•ò'));
    }
  };

  // ‚úÖ Î∞∞ÏÜ° Ï∂îÏ†Å
  const fetchTracking = async () => {
    if (!courier || !tracking) {
      alert('Î∞∞ÏÜ° Ï∂îÏ†ÅÏùÑ ÏúÑÌï¥ ÌÉùÎ∞∞ÏÇ¨ÏôÄ Ïö¥ÏÜ°Ïû• Î≤àÌò∏Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    try {
      const res = await axios.get(`/tracking?code=${courier}&invoice=${tracking}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setTrackingInfo(res.data);
    } catch (err) {
      alert('Î∞∞ÏÜ° Ï∂îÏ†Å Ïã§Ìå®: ' + (err.response?.data?.message || 'API Ïò§Î•ò'));
    }
  };

  // ‚úÖ Ï†ïÏÇ∞ Í≥ÑÏ¢å Îì±Î°ù
  const handleAccountSubmit = async () => {
    if (!bank || !account) {
      alert('ÏùÄÌñâÎ™ÖÍ≥º Í≥ÑÏ¢åÎ≤àÌò∏Î•º Î™®Îëê ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    try {
      await updatePost({
        settlementAccount: { bank, account },
        sellerStatus: 'Ï†ïÏÇ∞'
      });
      alert('üí∞ Ï†ïÏÇ∞ Í≥ÑÏ¢åÍ∞Ä Îì±Î°ùÎêòÏóàÏäµÎãàÎã§.');
    } catch (err) {
      alert('Í≥ÑÏ¢å Îì±Î°ù Ïã§Ìå®: ' + (err.response?.data?.message || 'ÏÑúÎ≤Ñ Ïò§Î•ò'));
    }
  };

  return (
    <div className={styles.dispatchBox}>
      <h4>üì¶ ÌåêÎß§Ïûê</h4>

      {/* üì¶ ÏÜ°Ïû• ÏûÖÎ†• */}
      {canInputShipping && (
        <>
          <ShippingForm
            courier={courier}
            tracking={tracking}
            setCourier={setCourier}
            setTracking={setTracking}
          />
          <button onClick={handleDispatch} className={styles.confirmBtn}>
            üì¶ Î∞úÏÜ° Ï≤òÎ¶¨
          </button>
        </>
      )}

      {/* ‚úèÔ∏è ÏÜ°Ïû• ÏàòÏ†ï & Î∞∞ÏÜ° Ï∂îÏ†Å */}
      {canModifyShipping && (
        <>
          {!isEditing ? (
            <div className={styles.buttonGroup}>
              <button onClick={() => setIsEditing(true)}>‚úèÔ∏è ÏÜ°Ïû• ÏàòÏ†ï</button>
              <button onClick={fetchTracking}>üîç Î∞∞ÏÜ° Ï∂îÏ†Å</button>
            </div>
          ) : (
            <>
              <ShippingForm
                courier={courier}
                tracking={tracking}
                setCourier={setCourier}
                setTracking={setTracking}
              />
              <div className={styles.buttonGroup}>
                <button onClick={handleTrackingUpdate}>‚úÖ ÏàòÏ†ï ÏôÑÎ£å</button>
                <button onClick={() => setIsEditing(false)}>‚ùå Ï∑®ÏÜå</button>
              </div>
            </>
          )}
        </>
      )}

      {/* üîç Î∞∞ÏÜ° Ï∂îÏ†Å Í≤∞Í≥º */}
      {canModifyShipping && trackingInfo && (
        <TrackingStatus trackingInfo={trackingInfo} />
      )}

      {/* ‚è≥ Í≤ÄÏÇ¨ Í≤∞Í≥º ÎåÄÍ∏∞ */}
      {isWaitingForInspection && (
        <div className={styles.notice}>
          ‚è≥ Í¥ÄÎ¶¨ÏûêÍ∞Ä Î¨ºÌíàÏùÑ ÌôïÏù∏ Ï§ëÏûÖÎãàÎã§.
        </div>
      )}
      {canSubmitErr && (
        <div className={styles.small_noti}>
          ‚ñ† Î¨ºÌíàÏóê Ïù¥ÏÉÅÏù¥ Î∞úÍ≤¨ÎêòÏóàÏäµÎãàÎã§. Íµ¨Îß§ÏûêÏôÄ Ïó∞ÎùΩÏùÑ Í∂åÌï©ÎãàÎã§.
        </div>
      )}

      {/* üí∞ Ï†ïÏÇ∞ Í≥ÑÏ¢å ÏûÖÎ†• */}
      {canSubmitAccount && (
        <div className={styles.notice}>
          <h5>üí∞ ÌåêÎß§ ÎåÄÍ∏àÏùÑ Î∞õÏúºÏã§ Í≥ÑÏ¢åÎ•º ÏûÖÎ†•Ìï¥ Ï£ºÏÑ∏Ïöî</h5>
          <input
            type="text"
            placeholder="ÏùÄÌñâÎ™Ö"
            value={bank}
            onChange={(e) => setBank(e.target.value)}
            className={styles.input}
          />
          <input
            type="text"
            placeholder="Í≥ÑÏ¢åÎ≤àÌò∏"
            value={account}
            onChange={(e) => setAccount(e.target.value)}
            className={styles.input}
          />
          <button onClick={handleAccountSubmit} className={styles.confirmBtn}>
            ‚úÖ Í≥ÑÏ¢å ÏûÖÎ†•
          </button>
        </div>
      )}

      {/* üí≥ Ï†ïÏÇ∞ Ï†ïÎ≥¥ ÌëúÏãú */}
      {sellerStatus === 'Ï†ïÏÇ∞' && (
        <div className={styles.notice}>
          <p>üí≥ Í≤∞Ï†ú Î∞©Ïãù: {post.paymentMethod === 'card' ? 'Ïπ¥Îìú Í≤∞Ï†ú' : 'Î¨¥ÌÜµÏû•ÏûÖÍ∏à'}</p>
          {post.settlementAccount?.bank && (
            <p>üí∞ Ï†ïÏÇ∞ Í≥ÑÏ¢å: {post.settlementAccount.bank} / {post.settlementAccount.account}</p>
          )}
        </div>
      )}

      {/* üéâ Ï†ïÏÇ∞ ÌôïÏù∏ */}
      {canFinalize && (
        <button
          onClick={() => updatePost({ sellerStatus: 'ÏôÑÎ£å' })}
          className={styles.confirmBtn}
        >
          üéâ Ï†ïÏÇ∞ ÌôïÏù∏
        </button>
      )}
    </div>
  );
}
