        try {
            const res = await fetch(
            `https://info.sweettracker.co.kr/api/v1/trackingInfo?t_key=${apiKey}&t_code=${courier}&t_invoice=${tracking}`
            );
            const data = await res.json();

            if (data.status && data.invoiceNo) {
            setStatus(data);
