import { useState } from 'react';
import styles from '../css/Posts.module.css';

export default function AdminActions({ post, updatePost }) {
  const [inspectionResult, setInspectionResult] = useState(post.inspectionResult || '');
  const [description, setDescription] = useState(post.inspectionDescription || '');
  const [courier, setCourier] = useState(post.shipping?.courier || '');
  const [tracking, setTracking] = useState(post.shipping?.tracking || '');

  const { buyerStatus, sellerStatus, inspectionResult: savedResult } = post;

  // ‚úÖ ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏ Ìï∏Îì§Îü¨
  const updateStatus = async (type, nextStatus, extra = {}) => {
    const patch = type === 'buyer'
      ? { buyerStatus: nextStatus }
      : { sellerStatus: nextStatus, ...extra };

    try {
      await updatePost(patch);
      alert(`${type === 'buyer' ? 'üßæ Íµ¨Îß§Ïûê' : 'üì¶ ÌåêÎß§Ïûê'} ÏÉÅÌÉúÍ∞Ä '${nextStatus}'Î°ú Î≥ÄÍ≤ΩÎêòÏóàÏäµÎãàÎã§.`);
    } catch (err) {
      alert(`${type === 'buyer' ? 'Íµ¨Îß§Ïûê' : 'ÌåêÎß§Ïûê'} ÏÉÅÌÉú Î≥ÄÍ≤Ω Ïã§Ìå®: ${err.response?.data?.message || 'ÏÑúÎ≤Ñ Ïò§Î•ò'}`);
    }
  };

  // ‚úÖ Í≤ÄÏÇ¨ Í≤∞Í≥º Ï†ÄÏû•
  const handleInspectionSubmit = async () => {
    if (!inspectionResult) return alert('Í≤ÄÏÇ¨ Í≤∞Í≥ºÎ•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.');
    if (inspectionResult === 'Ïù¥ÏÉÅ' && !description.trim()) {
      return alert('Ïù¥ÏÉÅ ÎÇ¥Ïö© ÏÑ§Î™ÖÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
    }

    try {
      await updatePost({
        inspectionResult,
        inspectionDescription: inspectionResult === 'Ïù¥ÏÉÅ' ? description : ''
      });
      alert('üß™ Í≤ÄÏÇ¨ Í≤∞Í≥ºÍ∞Ä Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§.');
    } catch (err) {
      alert('Í≤ÄÏÇ¨ Í≤∞Í≥º Ï†ÄÏû• Ïã§Ìå®: ' + (err.response?.data?.message || 'ÏÑúÎ≤Ñ Ïò§Î•ò'));
    }
  };

  // ‚úÖ ÏÜ°Ïû• ÏûÖÎ†• Î∞è Ï∂úÍ≥† Ï≤òÎ¶¨
  const handleShippingSubmit = async () => {
    if (!courier.trim() || !tracking.trim()) {
      return alert('ÌÉùÎ∞∞ÏÇ¨ÏôÄ ÏÜ°Ïû•Î≤àÌò∏Î•º Î™®Îëê ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
    }

    try {
      await updatePost({
        shipping: {
          courier,
          tracking,
          dispatchedAt: new Date()
        },
        buyerStatus: 'Ï∂úÍ≥†'
      });
      alert('üöö Ï∂úÍ≥† Ï≤òÎ¶¨ ÏôÑÎ£å');
    } catch (err) {
      alert('Ï∂úÍ≥† Ï≤òÎ¶¨ Ïã§Ìå®: ' + (err.response?.data?.message || 'ÏÑúÎ≤Ñ Ïò§Î•ò'));
    }
  };

  return (
    <div className={styles.adminBox}>
      <h4>üõ†Ô∏è Í¥ÄÎ¶¨Ïûê</h4>

      {/* üßæ Íµ¨Îß§Ïûê ÌùêÎ¶Ñ */}
      <div className={styles.subBox}>
        <h5>üßæ Íµ¨Îß§Ïûê</h5>
        {buyerStatus === 'ÏûÖÍ∏à' && (
          <button onClick={() => updateStatus('buyer', 'ÏûÖÍ∏àÌôïÏù∏')}>‚úÖ ÏûÖÍ∏à ÌôïÏù∏</button>
        )}
        {buyerStatus === 'ÏûÖÍ∏àÌôïÏù∏' && (
          <button onClick={() => updateStatus('buyer', 'Î¨ºÌíàÌôïÏù∏')}>üîç Î¨ºÌíà ÌôïÏù∏ Ï≤òÎ¶¨</button>
        )}
        {buyerStatus === 'Î¨ºÌíàÌôïÏù∏' && savedResult !== null && (
          <button onClick={() => updateStatus('buyer', 'Íµ¨Îß§')}>üõí Íµ¨Îß§Ïûê Íµ¨Îß§ ÏÑ†ÌÉù</button>
        )}
        {buyerStatus === 'Íµ¨Îß§' && (
          <div className={styles.subBox}>
            <h5>üöö ÏÜ°Ïû• ÏûÖÎ†•</h5>
            <input
              type="text"
              placeholder="ÌÉùÎ∞∞ÏÇ¨"
              value={courier}
              onChange={(e) => setCourier(e.target.value)}
              className={styles.input}
            />
            <input
              type="text"
              placeholder="ÏÜ°Ïû•Î≤àÌò∏"
              value={tracking}
              onChange={(e) => setTracking(e.target.value)}
              className={styles.input}
            />
            <button onClick={handleShippingSubmit}>üöö Ï∂úÍ≥† Ï≤òÎ¶¨</button>
          </div>
        )}
        {buyerStatus === 'Ï∂úÍ≥†' && (
          <button onClick={() => updateStatus('buyer', 'ÏôÑÎ£å')}>üéâ Î¨ºÌíà ÏàòÎ†π</button>
        )}
      </div>

      {/* üì¶ ÌåêÎß§Ïûê ÌùêÎ¶Ñ */}
      <div className={styles.subBox}>
        <h5>üì¶ ÌåêÎß§Ïûê</h5>
        {sellerStatus === 'Î∞úÏÜ°' && (
          <button onClick={() => updateStatus('seller', 'ÏûÖÍ≥†', {
            shipping: { courier: '', tracking: '', dispatchedAt: null }
          })}>
            üì• ÏûÖÍ≥† ÌôïÏù∏ (ÏÜ°Ïû• Ï¥àÍ∏∞Ìôî)
          </button>
        )}
        {sellerStatus === 'ÏûÖÍ≥†' && (
          <button onClick={() => updateStatus('seller', 'Î¨ºÌíàÌôïÏù∏')}>üîç Î¨ºÌíà ÌôïÏù∏ Ï≤òÎ¶¨</button>
        )}
        {sellerStatus === 'Î¨ºÌíàÌôïÏù∏' && (
          <>
            <label>
              üß™ Î¨ºÌíà Ïù¥ÏÉÅ Ïú†Î¨¥:
              <select
                value={inspectionResult}
                onChange={(e) => setInspectionResult(e.target.value)}
                className={styles.input}
              >
                <option value="">ÏÑ†ÌÉùÌïòÏÑ∏Ïöî</option>
                <option value="Ï†ïÏÉÅ">ÌåêÎß§ ÏÇ¨ÏßÑÍ≥º ÎèôÏùºÌï®</option>
                <option value="Ïù¥ÏÉÅ">ÌåêÎß§ ÏÇ¨ÏßÑÍ≥º ÏÉÅÌÉúÏôÄ Íµ¨ÏÑ±ÌíàÏù¥ ÏùºÏπòÌïòÏßÄ ÏïäÏõÄ</option>
              </select>
            </label>

            {inspectionResult === 'Ïù¥ÏÉÅ' && (
              <textarea
                placeholder="ÌåêÎß§ Ìè¨Ïä§ÌåÖÏùò ÎÇ¥Ïö©Í≥º Ïñ¥ÎñªÍ≤å Îã§Î•∏ÏßÄ ÏÑúÏà†"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                className={styles.textarea}
              />
            )}

            <button onClick={handleInspectionSubmit} className={styles.confirmBtn}>
              ‚úÖ Í≤ÄÏÇ¨ Í≤∞Í≥º Ï†ÄÏû•
            </button>

            {savedResult !== null && (
              <button onClick={() => updateStatus('seller', 'Ï†ïÏÇ∞')}>üí∞ Ï†ïÏÇ∞ ÌôïÏù∏</button>
            )}
          </>
        )}
        {sellerStatus === 'Ï†ïÏÇ∞' && (
          <button onClick={() => updateStatus('seller', 'ÏôÑÎ£å')}>üéâ Ï†ïÏÇ∞ ÏôÑÎ£å</button>
        )}
      </div>
    </div>
  );
}
