import { useState } from 'react';
import styles from '../css/Posts.module.css';
import TrackingStatus from './TrackingStatus';
import imageLibrary from '../data/images';

export default function BuyerActions({ post, updatePost }) {
  const [paymentMethod, setPaymentMethod] = useState(post.paymentMethod || '');
  const [trackingInfo, setTrackingInfo] = useState(null);

  const { buyerStatus, inspectionResult, shipping } = post;

  // ‚úÖ ÏÉÅÌÉú Î∂ÑÍ∏∞
  const canRequestDeposit = !buyerStatus || buyerStatus === 'ÏßÑÌñâ Ï†Ñ';
  const isWaitingForInspection = ['ÏûÖÍ∏àÌôïÏù∏', 'Î¨ºÌíàÌôïÏù∏'].includes(buyerStatus) && inspectionResult === null;
  const canDecisionOnInspection = buyerStatus === 'Î¨ºÌíàÌôïÏù∏' && inspectionResult !== null;
  const canTrackShipping = buyerStatus === 'Ï∂úÍ≥†' && shipping?.courier && shipping?.tracking;
  const canFinalize = buyerStatus === 'Ï∂úÍ≥†';

  // ‚úÖ ÌòÑÍ∏à ÏûÖÍ∏à Ï≤òÎ¶¨
  const handleDepositRequest = () => {
    if (!paymentMethod) {
      alert('Í≤∞Ï†ú Î∞©ÏãùÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }
    updatePost({ buyerStatus: 'ÏûÖÍ∏à', paymentMethod: 'cash' });
  };

  // ‚úÖ Íµ¨Îß§ / Ï∑®ÏÜå Í≤∞Ï†ï
  const handlePurchaseConfirm = () => updatePost({ buyerStatus: 'Íµ¨Îß§' });
  const handleCancelPurchase = () => updatePost({ buyerStatus: 'Ï∑®ÏÜå' });

  // ‚úÖ Í±∞Îûò ÏôÑÎ£å
  const handleFinalize = () => updatePost({ buyerStatus: 'ÏôÑÎ£å' });

  // ‚úÖ Î∞∞ÏÜ° Ï∂îÏ†Å
  // const fetchTracking = async () => {
  //   try {
  //     const res = await fetch(`/tracking?code=${shipping.courier}&invoice=${shipping.tracking}`);
  //     const data = await res.json();
  //     setTrackingInfo(data);
  //   } catch (err) {
  //     alert('Î∞∞ÏÜ° Ï∂îÏ†Å Ïã§Ìå®: ' + (err.message || 'API Ïò§Î•ò'));
  //   }
  // };
  const fetchTracking = async () => {
    try {
      const res = await fetch(`/tracking?code=${shipping.courier}&invoice=${shipping.tracking}`);
      const text = await res.text(); // üëà JSON ÎåÄÏã† textÎ°ú Î∞õÏïÑÎ≥¥Í∏∞
      console.log('üì¶ Î∞∞ÏÜ° ÏùëÎãµ ÏõêÎ¨∏:', text);

      try {
        const data = JSON.parse(text);
        setTrackingInfo(data);
      } catch (parseErr) {
        console.error('‚ùå JSON ÌååÏã± Ïã§Ìå®:', parseErr.message);
        alert('Î∞∞ÏÜ° ÏùëÎãµÏù¥ JSONÏù¥ ÏïÑÎãôÎãàÎã§.');
      }
    } catch (err) {
      alert('Î∞∞ÏÜ° Ï∂îÏ†Å Ïã§Ìå®: ' + (err.message || 'API Ïò§Î•ò'));
    }
  };

  return (
    <div className={styles.actionBox}>
      <h4>üßæ Íµ¨Îß§Ïûê</h4>

      {/* ‚úÖ Í≤∞Ï†ú Î∞©Ïãù ÏÑ†ÌÉù Î∞è ÏûÖÍ∏à ÏöîÏ≤≠ */}
      {canRequestDeposit && (
        <>
          {/* <select
            value={paymentMethod}
            onChange={e => setPaymentMethod(e.target.value)}
            className={styles.input}
          >
            <option value="">Í≤∞Ï†ú Î∞©Ïãù ÏÑ†ÌÉù</option>
            <option value="cash">ÌòÑÍ∏à ÏûÖÍ∏à</option>
            <option value="pay">ÎÑ§Ïù¥Î≤ÑÌéòÏù¥</option>
          </select> */}

          {paymentMethod === 'cash' && (
            <div className={styles.notice}>
              <p><img
                  src={imageLibrary.find(img => img.id === 2).src}
                  alt={imageLibrary.find(img => img.id === 2).alt}
                  className={styles.notiBank}
                />
                <span onClick={() => {navigator.clipboard.writeText("100041715312");alert('üìã ÏûÖÍ∏àÍ≥ÑÏ¢åÍ∞Ä Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§');
              }}> 1000-4171-5312</span> Ïù¥ÌòÑ
              </p>
              <p style={{ color: "#d65c5cff", marginLeft: "16px", marginBottom: "5px" }}><span style={{ fontSize: "12px" }}>üìå</span>ÏûÖÍ∏à ÌõÑ "ÏûÖÍ∏àÏôÑÎ£å"Î•º Íº≠ ÎàåÎü¨Ï£ºÏÑ∏Ïöî!</p>{' '}
              <button onClick={handleDepositRequest} className={styles.confirmBtn}>ÏûÖÍ∏àÏôÑÎ£å</button>
            </div>
          )}

          {post.paymentMethod === 'pay' && post.smartstoreProductId && (
            <a
              href={`https://smartstore.naver.com/grinform/products/${post.smartstoreProductId}`}
              target="_blank"
              rel="noopener noreferrer"
              className={styles.confirmBtn}
            >
              Í≤∞Ï†úÌïòÍ∏∞
            </a>
          )}
        </>
      )}

      {/* ‚úÖ Í≤ÄÏÇ¨ Í≤∞Í≥º ÎåÄÍ∏∞ Ï§ë */}
      {isWaitingForInspection && (
        <div className={styles.notice}>
          ‚è≥ Í¥ÄÎ¶¨ÏûêÍ∞Ä Î¨ºÌíàÏùÑ ÌôïÏù∏ Ï§ëÏûÖÎãàÎã§.
        </div>
      )}

      {/* ‚úÖ Í≤ÄÏÇ¨ Í≤∞Í≥º Í∏∞Î∞ò Íµ¨Îß§ Í≤∞Ï†ï */}
      {canDecisionOnInspection && (
        <div className={styles.notice}>
          <p>üîç Í¥ÄÎ¶¨Ïûê Í≤ÄÏÇ¨ Í≤∞Í≥º: <strong>{inspectionResult}</strong></p>
          {post.inspectionDescription && (
            <p className={styles.descriptionBox}>
              <strong>ÏÑ§Î™Ö:</strong> {post.inspectionDescription} <br />
              <span className={styles.small_noti}> ‚ñ† ÌåêÎß§ÏûêÏôÄ Ïó∞ÎùΩÏùÑ Í∂åÌï©ÎãàÎã§.</span>
            </p>
          )}
          <div className={styles.buttonGroup}>
            <button onClick={handlePurchaseConfirm} className={styles.confirmBtn}>‚úÖ Íµ¨Îß§</button>
            <button onClick={handleCancelPurchase} className={styles.confirmBtn}>‚ùå Ï∑®ÏÜå</button>
          </div>
        </div>
      )}

      {/* ‚úÖ Î∞∞ÏÜ° Ï∂îÏ†Å */}
      {canTrackShipping && (
        <div className={styles.buttonGroup}>
          <button onClick={fetchTracking}>üîç Î∞∞ÏÜ° Ï∂îÏ†Å</button>
        </div>
      )}

      {/* ‚úÖ Î∞∞ÏÜ° Ï∂îÏ†Å Í≤∞Í≥º */}
      {trackingInfo && <TrackingStatus trackingInfo={trackingInfo} />}

      {/* ‚úÖ Í±∞Îûò ÏôÑÎ£å */}
      {canFinalize && (
        <button onClick={handleFinalize} className={styles.confirmBtn}>
          üéâ Î¨ºÌíàÏàòÎ†π
        </button>
      )}
    </div>
  );
}
