import { useState, useEffect } from 'react';
import axios from '../api/axios';
import styles from '../css/Dashboard.module.css';

const ITEMS_PER_PAGE = 20;

export default function UserList({ isAdmin }) {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const formatPhone = (phone) => phone?.slice(-8) || 'Ï†ïÎ≥¥ ÏóÜÏùå';

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const res = await axios.get('/users', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });

        if (Array.isArray(res.data)) {
          setUsers(res.data);
        } else {
          setError(res.data.message || 'ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.');
          setUsers([]); // filter Î∞©Ïñ¥
        }
      } catch (err) {
        console.error('ÏÇ¨Ïö©Ïûê Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:', err);
        setError('ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.');
        setUsers([]); // filter Î∞©Ïñ¥
      } finally {
        setLoading(false);
      }
    };

    fetchUsers();
  }, []);

  const filteredUsers = Array.isArray(users)
    ? users.filter((user) => formatPhone(user.phone).includes(searchTerm))
    : [];

  const paginated = filteredUsers.slice(
    (currentPage - 1) * ITEMS_PER_PAGE,
    currentPage * ITEMS_PER_PAGE
  );

  return (
    <div className={styles.card}>
      <div className={styles.listHeader}>
        <h3>ÏÇ¨Ïö©Ïûê Î¶¨Ïä§Ìä∏</h3>
        <input
          type="text"
          placeholder="Ï†ÑÌôîÎ≤àÌò∏ ÎÅùÏûêÎ¶¨Î°ú Í≤ÄÏÉâ"
          value={searchTerm}
          onChange={(e) => {
            setSearchTerm(e.target.value);
            setCurrentPage(1);
          }}
          className={styles.searchInput}
        />
      </div>

      {loading ? (
        <p>‚è≥ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ëÏûÖÎãàÎã§...</p>
      ) : error ? (
        <p className={styles.error}>‚ö†Ô∏è {error}</p>
      ) : paginated.length === 0 ? (
        <p>üì≠ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.</p>
      ) : (
        <>
          <div className={styles.listContainer}>
            <div className={styles.listRowHeader}>
              <div></div>
              <div className={styles.list_tt}>ID</div>
              <div className={styles.list_td}>ÌôúÎèôÏùº</div>
              <div className={styles.list_td}>ÏûëÏÑ±Í∏Ä Ïàò</div>
            </div>

            {paginated.map((user) => (
              <div key={user._id} className={styles.listRow}>
                <div></div>
                <div className={styles.list_tt}>{formatPhone(user.phone)}</div>
                <div className={styles.list_td}>
                  {user.inactiveSince
                    ? new Date(user.inactiveSince).toLocaleDateString('ko-KR')
                    : 'Ï†ïÎ≥¥ ÏóÜÏùå'}
                </div>
                <div className={styles.list_td}>{user.postCount ?? 0}Í±¥</div>
              </div>
            ))}
          </div>

          {filteredUsers.length > ITEMS_PER_PAGE && (
            <div className={styles.pagination}>
              {Array.from(
                { length: Math.ceil(filteredUsers.length / ITEMS_PER_PAGE) },
                (_, i) => (
                  <button
                    key={i}
                    onClick={() => setCurrentPage(i + 1)}
                    className={currentPage === i + 1 ? styles.activePage : ''}
                  >
                    {i + 1}
                  </button>
                )
              )}
            </div>
          )}
        </>
      )}
    </div>
  );
}
