import { useState } from 'react';
import TrackingModal from './TrackingModal';
import styles from '../css/Posts.module.css';
import imageLibrary from '../data/images';

export default function BuyerActions({ post, updatePost }) {
  const {
    buyerStatus,
    inspectionResult,
    shipping,
    paymentMethod,
    smartstoreProductId,
    inspectionDescription,
  } = post;

  const [showTrackingModal, setShowTrackingModal] = useState(false);

  // ÏÉÅÌÉú Î∂ÑÍ∏∞
  const canRequestDeposit = !buyerStatus || buyerStatus === 'ÏßÑÌñâ Ï†Ñ';
  const isWaitingForInspection =
    ['ÏûÖÍ∏àÌôïÏù∏', 'Î¨ºÌíàÌôïÏù∏'].includes(buyerStatus) && inspectionResult === null;
  const canDecisionOnInspection =
    buyerStatus === 'Î¨ºÌíàÌôïÏù∏' && inspectionResult !== null;
  const canTrackShipping =
    buyerStatus === 'Ï∂úÍ≥†' && shipping?.courier && shipping?.tracking;
  const canFinalize = buyerStatus === 'Ï∂úÍ≥†';

  // Ïï°ÏÖò Ìï∏Îì§Îü¨
  const handleDepositRequest = () => {
    if (!paymentMethod) {
      alert('Í≤∞Ï†ú Î∞©ÏãùÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }
    updatePost({ buyerStatus: 'ÏûÖÍ∏à', paymentMethod: 'cash' });
  };

  const handlePurchaseConfirm = () => updatePost({ buyerStatus: 'Íµ¨Îß§' });
  const handleCancelPurchase = () => updatePost({ buyerStatus: 'Ï∑®ÏÜå' });
  const handleFinalize = () => updatePost({ buyerStatus: 'ÏôÑÎ£å' });

  return (
    <div className={styles.actionBox}>
      <h4>üßæ Íµ¨Îß§Ïûê</h4>

      {/* Í≤∞Ï†ú Î∞©Ïãù Î∞è ÏûÖÍ∏à ÏöîÏ≤≠ */}
      {canRequestDeposit && (
        <>
          {paymentMethod === 'cash' && (
            <div className={styles.notice}>
              <p onClick={() => {
                  if (navigator?.clipboard?.writeText) {
                    navigator.clipboard.writeText('100041715312')
                      .then(() => alert('üìã ÏûÖÍ∏àÍ≥ÑÏ¢åÍ∞Ä Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§'))
                      .catch(() => alert('‚ùå Î≥µÏÇ¨Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§'));
                  } else {
                    alert('‚ùå Ïù¥ Î∏åÎùºÏö∞Ï†ÄÏóêÏÑúÎäî Î≥µÏÇ¨ Í∏∞Îä•ÏùÑ ÏßÄÏõêÌïòÏßÄ ÏïäÏäµÎãàÎã§');
                  }
                }}   
              >
                <img
                  src={imageLibrary.find((img) => img.id === 2).src}
                  alt={imageLibrary.find((img) => img.id === 2).alt}
                  className={styles.notiBank}
                />
                <span>1000-4171-5312</span> Ïù¥ÌòÑ
              </p>
              <p className={styles.small_noti}>
                <span style={{ fontSize: '12px' }}>üìå</span> ÏûÖÍ∏à ÌõÑ "ÏûÖÍ∏àÏôÑÎ£å"Î•º Íº≠ ÎàåÎü¨Ï£ºÏÑ∏Ïöî!
              </p>
              <button onClick={handleDepositRequest} className={styles.confirmBtn}>
                ÏûÖÍ∏àÏôÑÎ£å
              </button>
            </div>
          )}

          {paymentMethod === 'pay' && smartstoreProductId && (
            <a
              href={`https://smartstore.naver.com/grinform/products/${smartstoreProductId}`}
              target="_blank"
              rel="noopener noreferrer"
              className={styles.confirmBtn}
            >
              Í≤∞Ï†úÌïòÍ∏∞
            </a>
          )}
        </>
      )}

      {/* Í≤ÄÏÇ¨ Í≤∞Í≥º ÎåÄÍ∏∞ Ï§ë */}
      {isWaitingForInspection && (
        <div className={styles.notice}>‚è≥ Í¥ÄÎ¶¨ÏûêÍ∞Ä Î¨ºÌíàÏùÑ ÌôïÏù∏ Ï§ëÏûÖÎãàÎã§.</div>
      )}

      {/* Í≤ÄÏÇ¨ Í≤∞Í≥º Í∏∞Î∞ò Íµ¨Îß§ Í≤∞Ï†ï */}
      {canDecisionOnInspection && (
        <div className={styles.notice}>
          <p>üîç Í¥ÄÎ¶¨Ïûê Í≤ÄÏÇ¨ Í≤∞Í≥º: <strong>{inspectionResult}</strong></p>
          {inspectionDescription && (
            <p className={styles.descriptionBox}>
              <strong>ÏÑ§Î™Ö:</strong> {inspectionDescription} <br />
              <span className={styles.small_noti}> ‚ñ† ÌåêÎß§ÏûêÏôÄ Ïó∞ÎùΩÏùÑ Í∂åÌï©ÎãàÎã§.</span>
            </p>
          )}
          <div className={styles.buttonGroup}>
            <button onClick={handlePurchaseConfirm} className={styles.confirmBtn}>
              ‚úÖ Íµ¨Îß§
            </button>
            <button onClick={handleCancelPurchase} className={styles.confirmBtn}>
              ‚ùå Ï∑®ÏÜå
            </button>
          </div>
        </div>
      )}

      {/* Î∞∞ÏÜ° Ï∂îÏ†Å */}
      {canTrackShipping && (
        <div className={styles.buttonGroup}>
          <button onClick={() => setShowTrackingModal(true)}>üîç Î∞∞ÏÜ° Ï∂îÏ†Å</button>
        </div>
      )}

      {/* Î∞∞ÏÜ° Ï∂îÏ†Å Î™®Îã¨ */}
      {showTrackingModal && (
        <TrackingModal
          courier={shipping.courier}
          tracking={shipping.tracking}
          onClose={() => setShowTrackingModal(false)}
        />
      )}

      {/* Í±∞Îûò ÏôÑÎ£å */}
      {canFinalize && (
        <button onClick={handleFinalize} className={styles.confirmBtn}>
          üéâ Î¨ºÌíàÏàòÎ†π
        </button>
      )}
    </div>
  );
}
